sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue ==='') {
		g_form.hideFieldMsg('assigned_to',true);
		if (g_form.getValue('assigned_group') !== '') {
			//Blank out the current group if the assigned user is no longer defined
			g_form.clearValue('assigned_group');
		}
		return;
	} else if (oldValue==newValue && g_form.getValue('assigned_group)') !== '') {
		//If there's no change, only repopulate if the assigned group is currently null
		return;
	}

	//If the Assigned Value is defined, automatically load the default/first group the user is a part of
	// Instantiate Ajax request based on current value for assigned_to
	var ga = new GlideAjax('GetDefaultUserGroup');
    // Pass the Requested for sys_id and send request to the server
	ga.addParam('sysparm_name','getUserGroup');
    ga.addParam('sysparm_userID', newValue);
    ga.getXML(populateAssignedGroup);

    // When the response is back from the server
    function populateAssignedGroup(response){
        var value = response.responseXML.documentElement.getAttribute("answer");
		//g_form.addInfoMessage('response is ' + value);
		g_form.clearValue('assigned_group');
		if (value === '') {
			g_form.showFieldMsg('assigned_to', '[Assigned To] User is not a member of any Group(s).','info');
		} else {
			g_form.setValue('assigned_group', value);
			g_form.hideFieldMsg('assigned_to',true);
		}
    }
}
