var TimeLimitAjaxUtils = Class.create();
// Extend the global.AbstractAjaxProcessor class
TimeLimitAjaxUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
    getAllMembers: function() {
		return 'This is my response';

		var gr = new GlideRecord("sys_user_has_role_time_limited");
		//gr.addQuery('active', 'true');
		//gr.addQuery('user.active', 'true');
		//gr.addQuery('end_time','>=', new GlideDateTime().getLocalDate());
		gr.addEncodedQuery('active=true^user.active=true^end_timeISEMPTY^ORend_time>=javascript:gs.beginningOfToday()');

		var rowCount = 0;
		var oMem = {};
		var aMem = [];
		while (gr.next()) {
			//Concatenate to JSON elements
			//Rename to _date as table does not include time
			rowCount = rowCount + 1;
			oMem = {"member": gr.user + "",
					"start_date": gr.start_time.getLocalDate() + "",
					"end_date": gr.end_time.getLocalDate() + ""
				};
			aMem.push(oMem);
		}
		//return {"rowCount": rowCount, "mrvs": JSON.stringify(aMem)};
		return JSON.stringify(aMem);

	}, 

    type: 'TimeLimitAjaxUtils'
});
