sys_script_include.script:
var GetDefaultUserGroup = Class.create();
// Extend the global.AbstractAjaxProcessor class
GetDefaultUserGroup.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
    // Defines the getUserGroup function.  
    // Create a GlideRecord for the system user group table.
    // Use the sysparm_userID passed from the client side to retrieve a record from the group table that the user is a member of.
	// Return the first group found, if any
    getUserGroup: function() {
		var userGroups = new GlideRecord("sys_user_grmember");
		userGroups.addQuery("user",this.getParameter('sysparm_userID'));
		userGroups.query();
		while (userGroups.next()) {
			//Returns the ID of the group
			return userGroups.group;
		}

		//If not, return nothing
		return null;
    },
    type: 'GetDefaultUserGroup'
});
