function parseCheckedList() {
	var currNo = '';
	var tblName = '';
	var currUserID = '';
	var url ='https://dev276507.service-now.com/now/nav/ui/classic/params/target/x_58872_needit_needit_list.do%3Fsysparm_first_row%3D1%26sysparm_query%3Du_requested_for%253D0a826bf03710200044e0bfc8bcbe5d7a%26sysparm_view%3D';
	//var whenNeeded = new GlideDateTime();

	try {
		currNo = current.number;
		tblName = current.getTableName();
		currUserID = gs.getUserID();
		//whenNeeded.getLocalDate();
		//whenNeeded.addDaysLocalTime(1);

		//var gr = new GlideRecord(tblName);
		//gr.get('number',currNo);
		//gr.assigned_to = currUserID;
		//gr.u_when_needed = whenNeeded;
		//gr.update();

		current.assigned_to = currUserID;
		current.state = 10;
		current.update();

		gs.addInfoMessage('Record(' + currNo +  ') has been assigned to ' + gs.getUserDisplayName());
		action.setRedirectURL(url);

		//gs.info('Updated: ' + tblName + '; ' + currNo + '; ' + currUserID);
	}
	catch (err) {
		gs.error('Error: ' + err);
	}
	gs.info('testing2: ' + tblName + '; ' + currNo + '; ' + currUserID);
}
